INSERT INTO `templates` VALUES (1,'One column layout','','<!--Start Page Content -->\r\n\r\n<div class=\"row\">\r\n    <div class=\"col-md-10\">\r\n        <p>\r\n            Hallmark Insurance Company is dedicated to providing competitive rates and flexible coverage options for\r\n            non-standard personal auto and specialty homeowner insurance.\r\n        </p>\r\n\r\n        <p>\r\n            Our personal auto product is designed to provide affordable coverage for a variety of risks, including\r\n            drivers without prior insurance coverage and those with a suspended license or who require an SR22\r\n            financial responsibility filing. We also accept salvage-titled vehicles and operators with a foreign or\r\n            international license.\r\n        </p>\r\n\r\n        <p>\r\n            The specialty property program is designed for lower value dwellings including older, well-maintained\r\n            properties. We also offer multiple renters policy options, including a liability-only rentersÃ¢â‚¬â„¢ policy.*\r\n        </p><br><br>\r\n        <i>*Coverage availabity is determined by each state.</i>\r\n    </div>\r\n    <div class=\"col-md-2\">\r\n        <img src=\"images/am-best.jpg\">\r\n    </div>\r\n    \r\n</div>\r\n\r\n<img src=\"images/silver-car.jpg\">\r\n\r\n\r\n<!--End Page Content-->','<!--Start Page Content -->\r\n\r\n<div class=\"row\">\r\n    <div class=\"col-md-10\">\r\n        <p>\r\n            Hallmark Insurance Company is dedicated to providing competitive rates and flexible coverage options for\r\n            non-standard personal auto and specialty homeowner insurance.\r\n        </p>\r\n\r\n        <p>\r\n            Our personal auto product is designed to provide affordable coverage for a variety of risks, including\r\n            drivers without prior insurance coverage and those with a suspended license or who require an SR22\r\n            financial responsibility filing. We also accept salvage-titled veh